package br.com.senaisp.aula02;

public class ExerciciosAula02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        // Primeira expressão 
		double dblConta = 2.00 / 5.00;
		System.out.println("Resultado da primeira expressão é : "+ dblConta);
		// Segunda expressão
		double dblEqc1 = ((2.00 / 3.00) + 10.0);
		System.out.println("Resultado da segunda expressão é : "+ dblEqc1);
		// Terceira expressão
		double dblEqc2 = (1.00 / 5.00) * (3.00 / 4.00) + 5.00;
		System.out.println("Resultado da terceira expressão é : "+ dblEqc2);
		
	}

}
